import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'My First Angular';
  strInput : string = "Hello";
  source : string = "data:image/svg+xml;base64,";
  courseChoice = 0;
  courses : any[] = [
    { row : 1 ,duration:'10 hours' , name:'Typescript'},
    { row : 2 ,duration:'20 hours' , name:'Angular'},
    { row : 3 ,duration:'13 hours' , name:'Node JS'}
  ]
  changeTitle(){
    if(this.title=='My First Angular')
      this.title='This is my first angular';
    else{
      this.title='My First Angular';
    }
  }

  changeSearchValue(eventData: any){
    this.strInput = (<HTMLInputElement>eventData.target).value;
  }
}
